mapper.update:
update daily_statistics
set recommend_kcal = #{recommendKcal},
    weight = #{weight},
    goal_weight = #{goalWeight},
    target = #{target}
where user_id = #{userId} and date = #{date}

mapper.select:
select date, goal_weight
from daily_statistics
where user_id = #{userId}
  and date between #{startDate} and #{endDate}
  and goal_weight != 0
order by date

mapper.update:
update user
set current_weight = #{currentWeight}
where id = #{id}

mapper.select:
select recommend_kcal, target, is_achieved, user_id, date
from daily_statistics
where user_id = #{userId} and date = #{date}

mapper.update:
update daily_statistics
set is_achieved = #{isAchieved}
where user_id = #{userId} and date = #{date}

mapper.select:
select cheating_kcal
from daily_statistics
where user_id = #{userId} and date = #{date}

mapper.update:
update daily_statistics
set cheating_kcal = #{cheatingKcal},
    is_achieved = #{isAchieved}
where user_id = #{userId} and date = #{date}

update user
set cheating_day = #{cheatingDay}
where id = #{id}

mapper.select:
select cheating_day
from user
where id = #{userId}
